(21, 'politics', 'Ludwig von Mises Institut', 'Mises.cz sdružuje lidi, kteří věří ve svobodu, individualismus a ochranu soukromého vlastnictví.', 'http://www.mises.cz/', '/web/images/thumb_mises.png'),
(23, 'politics', 'Students For Liberty', 'Studenti za svobodu je nezisková organizace sdružující svobodomyslné studenty. ', 'http://www.studentsforlibertycz.cz', '/web/images/thumb_sfl.png'),
(24, 'work', 'GitHub', 'On-line úložiště mých zdrojových kódů', 'https://github.com/AloisSeckar', '/web/images/thumb_github.png'),
(25, 'work', 'Web - Stará Krč', 'Historický web s fotografiemi a informacemi o Staré Krči', 'http://krc-historie.cz', '/web/images/logo_krc.png'),
(26, 'misc', 'Historie - Stará Krč', 'Stránky věnované historii městské části Praha-Krč a přihlehlému okolí', 'http://krc-historie.cz', '/web/images/logo_krc.png'),
(27, 'misc', 'Hry - Star Wars: Online', 'MMORPG hra Star Wars: The Old Republic', 'http://www.swtor.com/', '/web/images/thumb_sw.png');
