INSERT INTO `elrh_versions` (`version`, `date`, `dscr`) VALUES
('1.0', '2008-07-01', 'Primitivní výchozí verze osobního webu. Vše sdruženo v&nbsp;jednom souboru index.php bez důrazu na grafickou stránku.'),
('2.0', '2012-01-31', '<strong>Nové, radikální vylepšení vzhledu a&nbsp;funkčnosti.</strong> Přidáno napojení na MySQL databázi, rozdělení do více stránek a skriptů, změněn webhosting. Nový unifikovaný CSS vzhled. Jednotný styl všech informativních stránek.<br />\r\n<strong>Přehled funkcionality:</strong>\r\n\r\n<ul>\r\n <li>\r\n  Index - Základní představení a&nbsp;kontakty.\r\n </li>\r\n <li>\r\n  Blog - Publikační systém pro vlastní články + jejich přehled. Články vkládané do MySQL přes phpMyAdmin. Zatím bez možnosti hodnotit či komentovat.\r\n </li>\r\n <li>\r\n  Práce - Rozcestník pro stránky týkající se pracovních činností. Stránky: Web, Programy, Rychlovky, Redakční činnost, Softball\r\n </li>\r\n <li>\r\n  Hudba - Rozcestník pro informativní stránky týkající se mé hudební tvorby. Stránky: Rhungil, Ashes To Wind, Ennorath, Ostatní\r\n </li>\r\n <li>\r\n  Odkazy - Rozcestník na zajímavé nebo pro mě nějak důležité weby. Odkazy vkládané do MySQL přes phpMyAdmin. \r\n </li>\r\n <li>\r\n  Ostatní - Rozcestník pro informativní stránky týkající se mých dalších činností. Stránky: Softball, Fotogalerie\r\n </li>\r\n</ul>'),
('2.0.1', '2012-02-06', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>\r\n  Přidána stránka s&nbsp;přehledem verzí + odkaz na index a&nbsp;do zápatí.\r\n </li>\r\n <li>\r\n  Zrušena stránka index2.php (relikt z&nbsp;minulého hostingu).\r\n </li>\r\n <li>\r\n  Unifikace stránek pomocí PHP funkcí pro výpis společných prvků.\r\n </li>\r\n <li>\r\n  Kontrola validity html kódu.\r\n </li>\r\n</ul>'),
('2.0.2', '2012-10-28', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>\r\n  Nové stránky "LITERÁRNÍ TVORBA" a&nbsp;"HISTORIE" v&nbsp;sekci "OSTATNÍ"\r\n </li>\r\n</ul>'),
('2.1', '2012-10-30', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>\r\n  Přidána možnost komentovat články a&nbsp;jiné stránky na blogu pomocí FB pluginu\r\n </li>\r\n <li>\r\n  Upraveno zobrazování titulku stránek (různé titulky dle zobrazené stránky)\r\n </li>\r\n</ul>'),
('2.2', '2013-11-11', '<strong>Kompletní předělání CSS vzhledu.</strong> Obskurní černý "fantasy" skin nahradila modernější a&nbsp;funkčnější zeleno-bílá. Stránky postupně převáděny a&nbsp;optimalizovány pro novou grafiku. Zlepšování kvality kódu.'),
('2.2.1', '2013-11-20', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>\r\n  Refaktoring kódu některých obsahových stránek\r\n </li>\r\n <li>\r\n  Umožněno sdílení některého obsahu přes FB a&nbsp;Twitter\r\n </li>\r\n <li>\r\n  Do navigace přidány odkazy na sociální média (díky <a href="http://www.designbolts.com/author/zee-que/">Zee Que</a>)\r\n </li>\r\n <li>\r\n  Přepracování stránky s&nbsp;fotogaleriemi\r\n </li>\r\n <li>\r\n  Blog - v&nbsp;titulku stránky se vypisuje název článku\r\n </li>\r\n</ul>'),
('2.3', '2014-01-27', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>\r\n  Filtrování podle kategoríí v&nbsp;Blogu\r\n </li>\r\n</ul>'),
('2.4', '2014-10-20', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>Nová grafika</li>  \r\n <li>Nové rozložení obsahu</li>\r\n <li>Nová stránka "KNIHOVNA"</li>\r\n <li>Přepracování obsahu některých stránek</li>\r\n <li>Odkaz na LinkedIn</li>\r\n</ul>'),
('2.5', '2015-03-29', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>Stránka "GLOSY"</li>  \r\n <li>Stránka "DENÍK POLITIKA"</li>\r\n</ul>'),
('2.5.1', '2015-08-06', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>Archivace stránky "Blog"</li>  \r\n <li>Stránky "Deník politika" a&nbsp;"Glosy" mají nový indikátor posledního vloženého příspěvku</li>\r\n</ul>'),
('3.0', '2015-12-10', '<strong>Velká změnová verze.</strong> Došlo ke kompletnímu překlopení webu do objektového PHP a&nbsp;byly provedeny s&nbsp;tím spojené nutné úpravy.<br /> <strong>Zásadní změny</strong>:\r\n\r\n<ul>\r\n <li>Nasazen nový MVC-systém napsaný v&nbsp;objektovém PHP vlastní výroby</li>\r\n <li>Nové menu s&nbsp;třemi hlavními oddíly a&nbsp;drop-down boxy pro stránky v&nbsp;kategorii</li>\r\n <li>Nové samostatné stránky "Kontakty"a&nbsp;"Mapa stránek"</li>\r\n <li>Přepracování zobrazování odkazů</li>\r\n</ul>'),
('3.0.1', '2015-12-19', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>\r\n  Přidána stránka "online přenosy" v sekci Politika.\r\n </li>\r\n <li>\r\n  Měřič Google Analytics\r\n </li>\r\n <li>\r\n  Drobné opravy kódu.\r\n </li>\r\n</ul>'),
('3.0.2', '2015-12-28', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>\r\n  Vylepšení a sjednocení CSS\r\n </li>\r\n <li>\r\n  Pročištění nepoužívaných souborů\r\n </li>\r\n <li>\r\n  Opravy chyb (onlines, připojení k db)\r\n </li>\r\n</ul>'),
('3.1', '2016-08-23', '<strong>Seznam úprav:</strong>\r\n\r\n<ul>\r\n <li>\r\n  Nová stránka BĚH se statistikami běhání\r\n </li>\r\n <li>\r\n  Admin přihlášení + formuláře pro snazší zadávání dat (běhy, deník politika, glosy, onlines)\r\n </li>\r\n</ul>');
